    public void Ajustar(JLabel label, ImageIcon icon){
        //esta funcion ajusta un icono(parametro) al tamaño del label (parametro)
        Icon icono = new ImageIcon(icon.getImage().getScaledInstance(label.getWidth(), label.getHeight(), Image.SCALE_DEFAULT));
        label.setIcon(icono);
        this.repaint(); 
    }
    
    public void restringir(){
        RestrictedTextField restricted3 = new RestrictedTextField(this.txtC);
        restricted3.setOnlyNums(true);
        restricted3.setLimit(8);
        
        RestrictedTextField restricted4 = new RestrictedTextField(this.txtT1);
        restricted4.setOnlyNums(true);
         restricted4.setLimit(11);
         
        RestrictedTextField restricted5 = new RestrictedTextField(this.txtT2);
        restricted5.setOnlyNums(true);
         restricted5.setLimit(11);
         
          RestrictedTextField restricted6 = new RestrictedTextField(this.txtN);
        restricted6.setOnlyText(true);
        restricted6.setLimit(15);
        
         RestrictedTextField restricted7 = new RestrictedTextField(this.txtE);
        restricted7.setLimit(30);
        
        
        
         
    }
    private boolean verificacion(){
            if (!"".equals(txtN.getText()) && !"".equals(txtC.getText()) && 
            !"".equals(txtT1.getText()) && !"".equals(txtD.getText()) && !"".equals(txtE.getText())) {
                return true; 
            }else{
                JOptionPane.showMessageDialog(rootPane, "Faltan Campos por llenar");
                return false; 
            }
    }
    private void Borrar(int tipo) {
        //Elimina campos llenos en el formulario
        switch (tipo){
            case 1:
                txtN.setText(null);
                txtCo.setText(null);
                txtC.setText(null);
                break;
            case 2:
                txtD.setText(null);
                txtT1.setText(null);
                txtT2.setText(null);
                txtE.setText(null);
                break;
            case 3:
                txtN.setText(null);
                txtCo.setText(null);
                txtC.setText(null);
                txtD.setText(null);
                txtT1.setText(null);
                txtT2.setText(null);
                txtE.setText(null);
                break;
        }
    }

