    private void btnAgActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgActionPerformed
    if (this.verificacion()){ 
        try {
            String sql = "insert into proveedores(nombre, rif, pais, descripcion,"
                    + "telefono1, telefono2, direccion, email, estado)"
                + "values(?,?,?,?,?,?,?,?,?)";
            PreparedStatement ps = operaciones.Ingresar(sql);
            ps.setString(1, txtN.getText().toLowerCase());
            ps.setString(2, cbxC.getSelectedItem()+"-"+txtC.getText());
            ps.setString(3, cbxP.getSelectedItem().toString().toLowerCase());
            ps.setString(4, txtCo.getText().toLowerCase());
            ps.setLong(5, Long.parseLong(txtT1.getText()));
            if(!"".equals(txtT2.getText())){
              ps.setLong(6, Long.parseLong(txtT2.getText()));  
            }else{
              ps.setLong(6, 0); 
            }
            ps.setString(7, txtD.getText().toLowerCase());
            ps.setString(8, txtE.getText().toLowerCase());
            ps.setString(9, "activo");
            
            int n = ps.executeUpdate();
                if (n > 0) {
                    JOptionPane.showMessageDialog(null, "PROVEEDOR INGRESADO CON EXITO");
                    this.Borrar(3);
                }
            operaciones.conn.close();   
            } catch (Exception e) {
            JOptionPane.showMessageDialog(null,"ERROR AL GUARDAR\ncodigo error:"+e.getMessage());
        }
}  
    }//GEN-LAST:event_btnAgActionPerformed

